import React from 'react';
import { withRouter } from "react-router-dom";

class Step extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            step: 1
        }
    }  

    redirect = () => {
        if (this.state.step === 4) {
            this.props.history.push('/endScreen');
            return;
        }

        fetch("http://localhost:8080/?id="+this.state.step).then( response => response.JSON() ).then( (response) => {
            console.log(response);
        } )

        var nextStep = this.state.step + 1;
        this.setState({
            step: nextStep
        })  
    }

    render() {
        return(
        <div className="container h-100 position-absolute">
            <div className="row align-items-center h-50">
                <div className="col-10 mx-auto text-center">
                </div>
            </div>
        <div className="row align-items-center h-25">
            <div className="col-10 mx-auto text-center">
                <button onClick={this.redirect} type="button" className="btn btn-dark btn-lg btn-block">Current step is {this.state.step}</button>
            </div>
        </div>
        </div>
        );}
    }

export default withRouter(Step);
