import React, { Fragment } from 'react';
import { withRouter } from "react-router-dom";

class Step extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            step: 1,
        }
    }

    componentDidMount() {
        //First time load
        this.fetchData();
    }
    
    fetchData = () => {
        fetch("http://localhost:8080/?id="+this.state.step).then( response => response.json() ).then( (response) => {
            console.log(response);
            this.setState({
                question: response.questions,
                answer: response.answers
            })
        });
    }

    redirect = () => {
        if (this.state.step === 4) {
            this.props.history.push('/endScreen');
            return;
        }

        var nextStep = this.state.step + 1;
        fetch("http://localhost:8080/?id="+nextStep).then( response => response.json() ).then( (response) => {
            console.log(response);
            this.setState({
                step: nextStep,
                question: response.questions,
                answer: response.answers
            })
        });
    }

    render() {
        if (this.state.answer) {
            return(
                <Fragment>
                    <div className="container h-100 position-absolute">
                        <div className="row align-items-center h-25">
                            <div className="col-10 mx-auto text-center">
                                Question {this.state.step} of 24
                            </div>
                        </div>
                        <div className="row align-items-center h-25">
                            <div className="col-10 mx-auto text-center">
                                {this.state.question}
                            </div>
                        </div>
                        <div className="row align-items-center h-25">
                            <div className="col mx-auto text-center">
                                <button onClick={this.redirect} type="button" className="btn btn-dark btn-lg btn-block">{this.state.answer[0]}</button>
                            </div>
                            <div className="col mx-auto text-center">
                                <button onClick={this.redirect} type="button" className="btn btn-dark btn-lg btn-block">{this.state.answer[1]}</button>
                            </div>
                            <div className="col mx-auto text-center">
                                <button onClick={this.redirect} type="button" className="btn btn-dark btn-lg btn-block">{this.state.answer[2]}</button>
                            </div>
                            <div className="col mx-auto text-center">
                                <button onClick={this.redirect} type="button" className="btn btn-dark btn-lg btn-block">{this.state.answer[3]}</button>
                            </div>
                        </div>
                        <div className="row align-items-center h-25">
                            <div className="col-10 mx-auto text-center">
                                <button onClick={this.redirect} type="button" className="btn btn-dark btn-lg btn-block">Next question</button>
                            </div>
                        </div>
                    </div>
                </Fragment>
            );
        } else {
            return (
                <Fragment>
                    <div className="container h-100 position-absolute">
                        <div className="row align-items-center h-50">
                            <div className="col-10 mx-auto text-center">
                                Loading ...
                            </div>
                        </div>
                    </div>
                </Fragment>
            );
        }    
            
        
    }
}

export default withRouter(Step);
